import requests
import json

# Dirección de la API
url = "http://172.174.241.114:8000/login"  # Reemplaza con la IP de la API

# Datos a enviar (puedes cambiar estos valores)
data = {
    "username": "",  # Cambia por el nombre de usuario que deseas
    "password": ""  # Cambia por la contraseña que deseas
}

# Cabeceras para indicar que el cuerpo de la solicitud es JSON
headers = {
    "Content-Type": "application/json"
}

# Intentar varias veces
for i in range(9999999):  # Intentar 10 veces
    print(f"Intento {i + 1}")

    # Enviar la solicitud POST
    response = requests.post(url, json=data, headers=headers)

    # Verificar el estado de la respuesta
    if response.status_code == 200:
        print("Respuesta de la API:")
        print(json.dumps(response.json(), indent=4))  # Imprimir el JSON de la respuesta de forma legible
    else:
        print(f"Error en la solicitud. Código de estado: {response.status_code}")
        print("Respuesta:", response.text)

    print("-" * 50)  # Separador entre intentos
